<?phpsession_start();if (isset($_REQUEST['email']) && $widget_parameters['email-address']) {  $to = $widget_parameters['email-address'];  $subject = "Contact Us";  $message = "<html><body><h2>" . $_REQUEST['name'] . "</h2><br><p style='font-family: Tahoma;'>" . $_REQUEST['message'] . "</p></body></html>";  $from = $_REQUEST['email'];  $headers = "From: " . $from . "\r\n";  $headers .= "Reply-To: " . $from . "\r\n";  //$headers .= "CC: susan@example.com\r\n";  $headers .= "MIME-Version: 1.0\r\n";  $headers .= "Content-Type: text/html; charset=UTF-8\r\n";  if (mail($to, $subject, $message, $headers)) {    ?>    <h4>      Your message has been sent, thank you.    </h4>    <?php    unset($_REQUEST['email']);  } else {    ?>    <h4>      Sorry, something went wrong    </h4>    <?php  }  return;}?><form id="ContactUsForm" action="" method="POST">  <div class="block-row">    <label>      Name    </label>    <input type="text" class="text-field" name="name">  </div>  <div class="block-row">    <label>      E-Mail    </label>    <input type="email" class="text-field" name="email" required>  </div>  <div class="block-row">    <label>      Message    </label>    <textarea class="text-field" name="message" required></textarea>  </div>  <div class="block-row">    <button type="submit">      Send    </button>    <button type="reset">      Clean    </button>  </div></form>