<?php$format = $widget_parameters["format"];if ($widget_parameters['feeder']) {  $feeder = json_decode($widget_parameters['feeder'], TRUE);  $content_id = $feeder["id"];  $field_id = $feeder["fieldId"];}else {  echo "<$format>&nbsp;</$format>";  return;}$language = "en";if ($_REQUEST["_language"]) {  $language = $_REQUEST["_language"];}$content = EWCore::call_cached_api("admin/api/content-management/ew-list-feeder-related-contents", [            "content_id" => $content_id,            "key"        => "admin_ContentManagement_language",            "value"      => $language        ])["data"];if (isset($content)) {  $contentFields = $content[0]["content_fields"];  if ($format === 'default') {    echo $contentFields[$field_id]["content"];  }  else {    echo "<$format> {$contentFields[$field_id]["content"]} </$format>";  }}else {  echo "<span style='color:red'>Text not found</span>";}